// Validate VPS webhook authentication and process message
const payload = $input.all()[0].json;

// Expected auth tokens (store these securely in environment variables)
const expectedTokens = {
  // Add your VPS tokens here - get them from each VPS webhook_config.json
  // Format: "vps-id": "auth-token"
  // Example: "london-server": "abc123token"
};

const vpsId = payload.vps_id;
const receivedToken = payload.auth_token;
const messageType = payload.message_type;

// Validate authentication
if (!expectedTokens[vpsId] || expectedTokens[vpsId] !== receivedToken) {
  return {
    json: {
      error: "Invalid authentication",
      vps_id: vpsId,
      status: 401
    }
  };
}

// Process different message types
const result = {
  vps_id: vpsId,
  vps_name: payload.vps_name,
  timestamp: payload.timestamp,
  message_type: messageType,
  data: payload.data,
  action: 'none'
};

switch(messageType) {
  case 'heartbeat':
    result.action = 'update_heartbeat';
    result.system_info = payload.data.system_info;
    break;

  case 'notification':
    result.action = 'process_notification';
    result.priority = payload.data.priority;
    result.notification_message = payload.data.message;
    // Forward high priority notifications
    if (payload.data.priority === 'high') {
      result.send_alert = true;
    }
    break;

  case 'status_update':
    result.action = 'update_status';
    result.status_data = payload.data;
    break;

  case 'reward_update':
    result.action = 'send_reward_notification';
    result.reward_message = payload.data.message;
    break;

  case 'error_alert':
    result.action = 'send_error_alert';
    result.error_message = payload.data.error_message;
    result.send_alert = true;
    break;

  case 'command_response':
    result.action = 'forward_command_response';
    result.command = payload.data.command;
    result.success = payload.data.success;
    result.command_result = payload.data.result;
    break;
}

return { json: result };

// Update VPS state in workflow static data
const input = $input.all()[0].json;
const workflowStaticData = this.getWorkflowStaticData('global');

// Initialize VPS list if not exists
if (!workflowStaticData.vpsList) {
  workflowStaticData.vpsList = {};
}

const vpsId = input.vps_id;
const now = new Date().toISOString();

// Initialize VPS entry if not exists
if (!workflowStaticData.vpsList[vpsId]) {
  workflowStaticData.vpsList[vpsId] = {
    name: input.vps_name,
    status: 'unknown',
    first_seen: now,
    last_heartbeat: null,
    last_status_update: null,
    last_notification: null,
    system_info: {}
  };
}

const vps = workflowStaticData.vpsList[vpsId];
vps.name = input.vps_name; // Update name in case it changed

// Update based on action type
switch(input.action) {
  case 'update_heartbeat':
    vps.status = 'online';
    vps.last_heartbeat = input.timestamp;
    vps.system_info = input.system_info || {};
    break;

  case 'update_status':
    vps.status = 'online';
    vps.last_status_update = input.timestamp;
    vps.status_data = input.status_data;
    break;

  case 'process_notification':
    vps.last_notification = input.timestamp;
    break;
}

// Check if VPS should be marked offline (no heartbeat in 10 minutes)
const tenMinutesAgo = new Date(Date.now() - 10 * 60 * 1000).toISOString();
if (vps.last_heartbeat && vps.last_heartbeat < tenMinutesAgo) {
  vps.status = 'offline';
}

// Return updated state
return {
  json: {
    vps_id: vpsId,
    vps_state: vps,
    all_vpses: workflowStaticData.vpsList,
    action: input.action,
    input: input
  }
};

// Process Telegram input (messages and callback queries)
const input = $input.all()[0].json;
const workflowStaticData = this.getWorkflowStaticData('global');

// Initialize if needed
if (!workflowStaticData.vpsList) {
  workflowStaticData.vpsList = {};
}

const vpsList = workflowStaticData.vpsList;
const isCallback = input.callback_query !== undefined;
const isMessage = input.message !== undefined;

let chatId, messageText, callbackData, userId;

if (isCallback) {
  chatId = input.callback_query.message.chat.id;
  callbackData = input.callback_query.data;
  userId = input.callback_query.from.id;
  messageText = callbackData;
} else if (isMessage) {
  chatId = input.message.chat.id;
  messageText = input.message.text;
  userId = input.message.from.id;
}

// Parse command/action
let action = 'unknown';
let selectedVps = null;
let command = null;

if (messageText) {
  if (messageText === '/start' || messageText === '/vpses') {
    action = 'show_vps_list';
  } else if (messageText === '/help') {
    action = 'show_help';
  } else if (messageText.startsWith('select_vps_')) {
    action = 'vps_selected';
    selectedVps = messageText.replace('select_vps_', '');
  } else if (messageText.startsWith('cmd_')) {
    const parts = messageText.split('_');
    if (parts.length >= 3) {
      action = 'execute_command';
      selectedVps = parts[1];
      command = parts.slice(2).join('_');
    }
  } else if (messageText === 'back_to_vps_list') {
    action = 'show_vps_list';
  }
}

// Generate VPS list for display
const vpsArray = Object.entries(vpsList).map(([id, data]) => {
  const status = data.status === 'online' ? 'üü¢' : 'üî¥';
  const lastSeen = data.last_heartbeat ? new Date(data.last_heartbeat) : null;
  const timeDiff = lastSeen ? Math.floor((Date.now() - lastSeen.getTime()) / 1000 / 60) : 999;

  return {
    id: id,
    name: data.name,
    status: status,
    display: `${status} ${data.name} (${timeDiff < 999 ? timeDiff + 'm ago' : 'offline'})`,
    online: data.status === 'online' && timeDiff < 10,
    last_seen_minutes: timeDiff
  };
}).sort((a, b) => a.name.localeCompare(b.name));

return {
  json: {
    action: action,
    chat_id: chatId,
    user_id: userId,
    message_text: messageText,
    selected_vps: selectedVps,
    command: command,
    vps_list: vpsArray,
    callback_data: callbackData,
    is_callback: isCallback
  }
};

// Generate VPS selection menu
const input = $input.all()[0].json;
const vpsList = input.vps_list;

if (vpsList.length === 0) {
  return {
    json: {
      chat_id: input.chat_id,
      message: "ü§ñ *Gensyn Multi-VPS Manager*\n\n‚ùå No VPSs currently available.\n\nPlease ensure your VPS bots are running and configured with the correct webhook URL.",
      parse_mode: "Markdown",
      reply_markup: null
    }
  };
}

// Create inline keyboard with VPS options
const keyboard = vpsList.map(vps => [{
  text: vps.display,
  callback_data: `select_vps_${vps.id}`
}]);

// Add refresh button
keyboard.push([{
  text: "üîÑ Refresh",
  callback_data: "show_vps_list"
}]);

const totalVpses = vpsList.length;
const onlineVpses = vpsList.filter(v => v.online).length;

return {
  json: {
    chat_id: input.chat_id,
    message: `ü§ñ *Gensyn Multi-VPS Manager*\n\nüìä Status: ${onlineVpses}/${totalVpses} VPSs online\n\nüñ•Ô∏è Select a VPS to manage:`,
    parse_mode: "Markdown",
    reply_markup: {
      inline_keyboard: keyboard
    },
    edit_message: input.is_callback
  }
};

// Generate VPS management menu
const input = $input.all()[0].json;
const vpsId = input.selected_vps;
const vpsList = input.vps_list;

// Find the selected VPS
const selectedVps = vpsList.find(v => v.id === vpsId);

if (!selectedVps) {
  return {
    json: {
      chat_id: input.chat_id,
      message: "‚ùå VPS not found. Please try again.",
      reply_markup: {
        inline_keyboard: [[
          { text: "‚¨ÖÔ∏è Back to VPS List", callback_data: "show_vps_list" }
        ]]
      }
    }
  };
}

// Get VPS details from static data
const workflowStaticData = this.getWorkflowStaticData('global');
const vpsData = workflowStaticData.vpsList[vpsId] || {};

// Build status info
const statusIcon = selectedVps.online ? 'üü¢' : 'üî¥';
const statusText = selectedVps.online ? 'Online' : 'Offline';
const lastSeen = selectedVps.last_seen_minutes < 999 ? `${selectedVps.last_seen_minutes}m ago` : 'unknown';

// System info if available
let systemInfo = '';
if (vpsData.system_info && Object.keys(vpsData.system_info).length > 0) {
  const sys = vpsData.system_info;
  systemInfo = `\nüíª CPU: ${sys.cpu_percent || '?'}% | RAM: ${sys.memory_percent || '?'}% | Disk: ${sys.disk_percent || '?'}%`;
}

// Create command buttons
const keyboard = [
  [
    { text: "üìä Status", callback_data: `cmd_${vpsId}_gensyn_status` },
    { text: "üåê Check IP", callback_data: `cmd_${vpsId}_check_ip` }
  ],
  [
    { text: "‚ñ∂Ô∏è Start Gensyn", callback_data: `cmd_${vpsId}_start_gensyn` },
    { text: "‚èπÔ∏è Stop Gensyn", callback_data: `cmd_${vpsId}_kill_gensyn` }
  ],
  [
    { text: "üîí VPN On", callback_data: `cmd_${vpsId}_vpn_on` },
    { text: "üîì VPN Off", callback_data: `cmd_${vpsId}_vpn_off` }
  ],
  [
    { text: "üìã Logs", callback_data: `cmd_${vpsId}_get_logs` },
    { text: "üîÑ Update", callback_data: `cmd_${vpsId}_soft_update` }
  ],
  [
    { text: "‚¨ÖÔ∏è Back to VPS List", callback_data: "show_vps_list" }
  ]
];

return {
  json: {
    chat_id: input.chat_id,
    message: `üñ•Ô∏è *${selectedVps.name} Management*\n\nüìä Status: ${statusIcon} ${statusText} (${lastSeen})${systemInfo}\n\nüéõÔ∏è Choose an action:`,
    parse_mode: "Markdown",
    reply_markup: {
      inline_keyboard: keyboard
    },
    edit_message: input.is_callback
  }
};

// Route command to VPS
const input = $input.all()[0].json;
const vpsId = input.selected_vps;
const command = input.command;

// VPS webhook configurations (add your VPS details here)
const vpsConfigs = {
  // Format: "vps-id": { "webhook_url": "http://vps-ip:port/webhook/command", "auth_token": "token" }
  // Example:
  // "london-server": {
  //   "webhook_url": "http://192.168.1.100:8080/webhook/command",
  //   "auth_token": "your-london-server-token"
  // }
};

const config = vpsConfigs[vpsId];
if (!config) {
  return {
    json: {
      error: `VPS configuration not found for ${vpsId}`,
      chat_id: input.chat_id,
      vps_id: vpsId
    }
  };
}

// Prepare command parameters based on command type
let parameters = {};
if (command === 'get_logs') {
  parameters = { log_type: 'gensyn', lines: 20 };
} else if (command === 'start_gensyn') {
  parameters = { use_sync_backup: true, fresh_start: false };
}

// Prepare command payload
const commandPayload = {
  command: command,
  parameters: parameters,
  auth_token: config.auth_token,
  timestamp: new Date().toISOString(),
  request_id: `${vpsId}-${Date.now()}`
};

return {
  json: {
    ...commandPayload,
    webhook_url: config.webhook_url,
    chat_id: input.chat_id,
    vps_id: vpsId,
    vps_name: input.vps_list.find(v => v.id === vpsId)?.name || vpsId
  }
};

// Process command response and format for Telegram
const commandInput = $input.all()[0].json;
const response = $input.all()[1]?.json || { error: 'No response received' };

let message;
let success = false;

if (response.error) {
  message = `‚ùå *Command Failed*\n\nüñ•Ô∏è VPS: ${commandInput.vps_name}\nüîß Command: \`${commandInput.command}\`\n\nüí• Error: ${response.error}`;
} else if (response.success === false) {
  message = `‚ùå *Command Failed*\n\nüñ•Ô∏è VPS: ${commandInput.vps_name}\nüîß Command: \`${commandInput.command}\`\n\nüìù Result:\n\`\`\`\n${response.result || 'Unknown error'}\n\`\`\``;
} else {
  success = true;
  let result = response.result || 'Command executed successfully';

  // Truncate very long results
  if (result.length > 1000) {
    result = result.substring(0, 1000) + '\n\n... (truncated)';
  }

  message = `‚úÖ *Command Successful*\n\nüñ•Ô∏è VPS: ${commandInput.vps_name}\nüîß Command: \`${commandInput.command}\`\n\nüìù Result:\n\`\`\`\n${result}\n\`\`\``;
}

// Add back button
const keyboard = [[
  { text: `üñ•Ô∏è Back to ${commandInput.vps_name}`, callback_data: `select_vps_${commandInput.vps_id}` },
  { text: "üìã VPS List", callback_data: "show_vps_list" }
]];

return {
  json: {
    chat_id: commandInput.chat_id,
    message: message,
    parse_mode: "Markdown",
    reply_markup: {
      inline_keyboard: keyboard
    },
    success: success,
    command: commandInput.command,
    vps_name: commandInput.vps_name
  }
};
